function lua_do()
function randCol(event)
    if (event.phase == "began")
    then
        event.target:setFillColor(math.random(), math.random(), math.random())
    end
    return true
end
function msg(event)
    if (event.phase == "began")
    then
        message(event.target.isImage and "touch icon" or ("touch text <s><s>"" .. event.target.text .. "<s><s>""))
    end
    return true
end
function tap(event)
    if (event.phase == "began")
    then
        if (not event.target.click)
        then
            event.target.click = true
            if (event.target.isImage)
            then
                transition.to(event.target, { time = 100, width = event.target.width <plus> 3, height = event.target.height <plus> 3, onComplete =
                function()
                    transition.to(event.target, { time = 100, width = event.target.width - 3, height = event.target.height - 3, onComplete =
                   function()
                        event.target.click = false
                    end })
                end })
            else
                transition.to(event.target, { time = 100, size = event.target.size <plus> 3, onComplete =
               function()
                    transition.to(event.target, { time = 100, size = event.target.size - 3, onComplete =
                    function()
                        event.target.click = false
                    end })
                end })
            end
        end
    end
    return true
end
end
